// 기본 api url 주소
const URL_API = "http://localhost:8080/api/v1";

// hello api url 주소
const URL_HELLO = `${URL_API}/hello`;

// user api url 주소
const URL_SURVEY = `${URL_API}/survey`;

function hello(){
    return fetch(URL_HELLO)
        .then(response => {
            return response.text();
        })
        .then(hello => {
            return hello;
        })
        .catch(error => console.log(error));
}

function getSurvey(){
    return fetch(URL_SURVEY)
        .then(response => {
            return response.json();
        })
        .then(surveyDTO => {
            return surveyDTO;
        })
        .catch(error => console.log(error));
}

export {
    hello,
    getSurvey
}
